function(doc) {
  if (doc.hasField("category")) {
    if (doc.getFirstFieldValue("category").equals("blog")) {
      doc.setField("type", "post");
    } else {
      doc.setField("type", "page");
    }
  } else {
    doc.setField("type", "page")
  }

  return doc;
}

function(doc){
  var id = doc.getId();
  if (id != null && id.indexOf("questions/tagged") != -1){
    print("Dropping " + id);
    return null;
  }
  return doc;
}

//given the last sub query, which gets us the top docs for the top
// users from the signals index, go get the full docs from the main
// collection.
var topRecSignalDocsRsp = ctx.getProperty('topRelatedSignalsDocs');
var theQuery = "*:*";

if (topRecSignalDocsRsp && topRecSignalDocsRsp.response && topRecSignalDocsRsp.response.numFound > 0){
  print(topRecSignalDocsRsp.response.numFound);
  var docs = topRecSignalDocsRsp.response.docs;
  //print(docs);
  theQuery = "{!lucene q.op=OR}(";
  //for each document, or it

  for (i = 0; i < docs.size(); i++){
    var doc = docs[i];
    print(doc.id);
    //since we are doing exact match, we only need one or the other
    request.putSingleParam("ss_" + i, doc.subject_simple_s);
    request.putSingleParam("tid_" + i, doc.threadId_s);
    theQuery += "({!term f=subject_simple v=$ss_" + i + "} AND {!term f=threadId v=$tid_" + i + "}) ";
  }
  theQuery += ")";
} else{
  //We don't have a response, so let's just get the most popular documents overall or some other sorting of the main content
  print("No response to work with");
}
print("The Query: ");
print(theQuery);
request.putSingleParam("q", theQuery);
request.putSingleParam("rows", "5");
request.putSingleParam("fq", "-in_reply_to:[* TO *]");

